    public static bool DecryptMythwarePassword(byte[] data, out string password)
    {
        password = null;

        try
        {
            if (data == null || data.Length < 8)
                return false;

            int dwordCount = data.Length / 4;

            for (int i = 0; i < dwordCount; i++)
            {
                int idx = i * 4;

                uint value =
                    (uint)(data[idx]
                    | (data[idx + 1] << 8)
                    | (data[idx + 2] << 16)
                    | (data[idx + 3] << 24));

                value ^= 0x50434C45u;
                value ^= 0x454C4350u;

                data[idx] = (byte)(value & 0xFF);
                data[idx + 1] = (byte)((value >> 8) & 0xFF);
                data[idx + 2] = (byte)((value >> 16) & 0xFF);
                data[idx + 3] = (byte)((value >> 24) & 0xFF);
            }

            int offset = data[0];

            if (offset < 0 || offset >= data.Length - 1)
                return false;

            var chars = new List<char>(32);

            for (int i = offset; i + 1 < data.Length; i += 2)
            {
                ushort w = (ushort)(data[i] | (data[i + 1] << 8));
                if (w == 0)
                    break;

                chars.Add((char)w);

                if (chars.Count >= 32)
                    break;
            }

            password = new string(chars.ToArray());
            return true;
        }
        catch
        {
            return false;
        }
    }

